mapper.select:
SELECT * FROM member
WHERE member_name LIKE CONCAT('%', #{searchKeyword}, '%')
ORDER BY member_date DESC LIMIT #{startRow} , #{listLimit}

SELECT * FROM member
	WHERE member_name = #{member_name}
		AND member_bday = #{member_bday}
		AND member_tel = #{member_tel}

mapper.update:
UPDATE member
	SET member_pw = #{member_pw}
		WHERE member_id = #{member_id}

mapper.select:
SELECT *
	FROM member
		WHERE 
			member_id = #{member_id}
			AND member_name = #{member_name}
			AND member_bday = #{member_bday}
			AND member_tel = #{member_tel}

mapper.insert:
