mapper.update:
UPDATE movieinfo
SET info_movie_code = #{info_movie_code}
, info_movie_title = #{info_movie_title}
, info_story = #{info_story}
, info_year = #{info_year}
, info_time = #{info_time}
, info_rating = #{info_rating }
, info_genre = #{info_genre }
, info_showdate = #{info_showdate}
, info_enddate = #{info_enddate}
, info_status = #{info_status}
WHERE info_movie_code = #{info_movie_code}

mapper.insert:
INSERT INTO schedule
VALUES (
(SELECT *
FROM (SELECT IFNULL(MAX(CAST(sch_code AS UNSIGNED)), 0) + 1
FROM schedule) A)
, #{sch_screen_code}
, #{sch_cinema_code}
, #{sch_movie_code}
, #{sch_register_date}
, #{sch_start_time}
, #{sch_last_time}
, 45
)

mapper.select:
SELECT sch_code , screen.screen_name , cinema.cinema_name , cinema.cinema_code , movieinfo.info_movie_code , movieinfo.info_movie_title , sch_movie_date , TIME_FORMAT(sch_start_time,'%H:%i')AS sch_start_time , TIME_FORMAT(sch_last_time,'%H:%i')AS sch_last_time FROM schedule LEFT JOIN screen ON screen.screen_code = sch_screen_code LEFT JOIN cinema ON cinema.cinema_code = sch_cinema_code LEFT JOIN movieinfo ON movieinfo.info_movie_code = sch_movie_code
AND info_movie_code = #{sch_movie_code}
AND cinema_code = #{sch_cinema_code}
AND sch_movie_date = #{sch_research_date}
LIMIT #{startRow} , #{listLimit}

SELECT COUNT(*) FROM ( SELECT sch_code , screen.screen_name , cinema.cinema_name , cinema.cinema_code , movieinfo.info_movie_code , movieinfo.info_movie_title , sch_movie_date , TIME_FORMAT(sch_start_time,'%H:%i')AS sch_start_time , TIME_FORMAT(sch_last_time,'%H:%i')AS sch_last_time FROM schedule LEFT JOIN screen ON screen.screen_code = sch_screen_code LEFT JOIN cinema ON cinema.cinema_code = sch_cinema_code LEFT JOIN movieinfo ON movieinfo.info_movie_code = sch_movie_code
AND info_movie_code = #{sch_movie_code}
AND cinema_code = #{sch_cinema_code}
AND sch_movie_date = #{sch_research_date}
) AS total_results

mapper.update:
UPDATE schedule
	SET
		  sch_movie_date = #{sch_movie_date}
		, sch_start_time = #{sch_start_time}
		, sch_last_time = #{sch_last_time}
WHERE sch_code = #{sch_code}

UPDATE reservation
SET res_date = #{res_date}
WHERE res_code = #{res_code}
